define([
	'dojo/dom',
	'dojo/on',
	'dojo/topic',
	'build82/config'
	], 
    function(dom, on, topic, Config) {
		var static = {
			focus: 'dialog-session_clear'
		},
				
		handleHide = function() {
			topic.publish(Config.Topic.Focus, 'pop', {interface:static.focus});
		},
				
		handleShow = function() {
			topic.publish(Config.Topic.Focus, 'push', {interface:static.focus, title:'Clear Session', alias:'session/clear'});
		},
				
		handleSubmit = function() {
			topic.publish(Config.Topic.Session, 'clear', {});
			handleHide();
		};
			
		return {
			Init: function() {
				on(dom.byId('nav_session_clear'), 'click', handleShow);
				on(dom.byId('session_clear_cancel'), 'click', handleHide);
				on(dom.byId('session_clear_submit'), 'click', handleSubmit);
			}
		};
	}
);