define([
	'dojo/query',
	'dojo/_base/fx',
	'dojo/fx',
	'dojo/fx/easing',
	'dojo/aspect',
	'dojo/NodeList-dom',
	'dojo/dom',
	'dojo/dom-construct',
	'dojo/dom-class',
	'dojo/dom-style',
	'dojo/on',
	'dojo/topic',
	'dojo/_base/array',
	'build82/config',
	'build82/menuitem'
	], 
    function(query, baseFx, fx, easing, aspect, nld, dom, domConstruct, domClass, domStyle, on, topic, baseArray,
		Config, MenuItem) {
		var static = {
			content: Config.Menu,
			featureOffset: 50,
			menuHandler: null
		},
				
		// clear content
		contentHide = function() {
			var contentAnimation_ary = []
			query('article').forEach(function(article) {
				contentAnimation_ary.push(baseFx.fadeOut({
					node: article,
					duration: 100
				}));
			});
			
			topic.publish(Config.Topic.Focus, 'pause');
			
			return contentAnimation_ary;
		},
				
		contentShow = function () {
			query('article').removeClass('hidden');

			// fade in content
			query('article').forEach(function(article) {
				baseFx.fadeIn({
					node: article,
					duration: 100
				}).play();
			});
			
			topic.publish(Config.Topic.Focus, 'resume');
		},
		
		/**
		 * Handle Menu click while menu is "closed"
		 * @param {type} param_evt Mouse Event, menu will not activate if link clicked
		 * @returns void
		 */
		handleMenuOn = function(param_evt) {
			if(!clickedAnchorTag(param_evt)) return;
			
			// create menu & animations
			var zipperAnimation_ary = [];
			baseArray.forEach(static.content, function(item, index) {
				var item_wgt = MenuItem(item);
				domConstruct.place(item_wgt.domNode, 'link_list', 'last');
				
				// animation start style & postiion
				domClass.add(item_wgt.domNode, 'animationStart');
				domStyle.set(item_wgt.domNode, 'left', static.featureOffset * (2 - (index % 2 * 3)) * index/10 + 'px');
				
				// slide in
				zipperAnimation_ary.push(baseFx.animateProperty({
					node: item_wgt.domNode,
					properties: {
						left: 0
					},
					easing: easing.sineOut,
					duration: 200,
					delay: 40 * index
				}));
				
				// fade in
				zipperAnimation_ary.push(baseFx.fadeIn({
					node: item_wgt.domNode,
					duration: 200,
					easing: easing.quadIn,
					delay: 40 * index
				}));
			});
			
			// animate menu
			var hideContent = fx.combine(contentHide());
			aspect.after(hideContent, 'onEnd', function() {
				query('article').addClass('hidden');
				query('nav').removeClass('hidden');
				fx.combine(zipperAnimation_ary).play();
			});
			hideContent.play();
			
			// set menu handle style
			domClass.add('menu', 'pulseNavAnim');
			
			// set new click event handler
			static.menuHandler.remove();
			static.menuHandler = on(dom.byId('menu'), 'click', function(evt) {
				handleMenuOff(evt);
			});
		},
				
		/**
		 * Handle Menu click while menu is "open"
		 * @param {type} param_evt Mouse Event, menu will not activate if link clicked
		 * @returns void
		 */
		handleMenuOff = function(param_evt) {
			if(!clickedAnchorTag(param_evt)) return;
			
			// construct animation
			var tearAnimation_ary = [];
			query('div.menuitem').forEach(function(feat, index) {
				// fade out
				tearAnimation_ary.push(baseFx.fadeOut({
					node: feat,
					easing: easing.cubicOut,
					duration: 100
				}));
				
				// slide out
				tearAnimation_ary.push(baseFx.animateProperty({
					node: feat,
					properties: {
						left: static.featureOffset * (2 - (index % 2 * 3))
					},
					duration: 100
				}));
			});
			
			// play & destroy menu
			var tear = fx.combine(tearAnimation_ary);
			aspect.after(tear, 'onEnd', function() {
				query('div.menuitem').forEach(domConstruct.destroy);
				query('nav').addClass('hidden');
				
				contentShow();
			});
			tear.play();
			
			// set menu handle style
			domClass.remove('menu', 'pulseNavAnim');
			
			// set new click event handler
			static.menuHandler.remove();
			static.menuHandler = on(dom.byId('menu'), 'click', function(evt) {
				handleMenuOn(evt);
			});
		},
				
		/**
		 * Show / Hide main page content 'article', links will not activate the behavoior
		 * @param {type} param_evt Mouse Event used to determine if link was clicked
		 * @returns {Boolean} returns true if competed, false if terminated
		 */
		clickedAnchorTag = function(param_evt) {
			// navigate to links in menu
			if(param_evt && param_evt.target.nodeName === 'A') {
				window.location.href = param_evt.target.href;
				return false;
			}
			
			return true;
		};
			
		return {
			Init: function() {
				// enable menu
				if(dom.byId('link_list')) {
					static.menuHandler = on(dom.byId('menu'), 'click', handleMenuOn);
					query('a[href="#menu"]').on('click', function() {handleMenuOn();});
				}
				
				topic.subscribe(Config.Topic.Focus, function(command) {
					switch(command) {
						// close menu on focus pop
						case 'push' :
						case 'hidden' :
							handleMenuOff();
							break;
					}
				});
			},
			/*
			 * Externally invoke handleMenuOn
			 */
			Show: handleMenuOn,
			/*
			 * Externally invoke handleMenuOff
			 */
			Hide: handleMenuOff
		};
	}
);